package com.exedio.cope;

import java.text.DecimalFormat;
import java.text.DecimalFormatSymbols;
import java.util.Locale;

/**
 * This class is for formatting consistently to contents of information_schema
 * in postgresql.
 */
final class PostgresqlFormat
{
	static String format(final double number)
	{
		final DecimalFormat df = new DecimalFormat(
				"0.0###################################################################################################################################################################################################################################################################################",
				doubleFormatSymbols);
		final String result = df.format(number);
		if(".0".equals(result))
			return "";
		if("-.0".equals(result))
			return "-";
		return result;
	}

	private static final DecimalFormatSymbols doubleFormatSymbols =
			DecimalFormatSymbols.getInstance(Locale.ENGLISH);


	private PostgresqlFormat()
	{
		// prevent instantiation
	}
}
